mapper.insert:


insert into ej_order (id, order_time, total, 
  customer_id, waiter_id, address_id
  )
values (#{id,jdbcType=BIGINT}, #{orderTime,jdbcType=BIGINT}, #{total,jdbcType=DOUBLE}, 
  #{customerId,jdbcType=BIGINT}, #{waiterId,jdbcType=BIGINT}, #{addressId,jdbcType=BIGINT}
  )

mapper.select:
select * from ej_order

    order_time like concat('%',#{orderTime} ,'%')
    and customer_id=#{customerId} 
    and waiter_id=#{waiterId} 
    and address_id=#{addressId}
